#ifndef IGT_PRIMES_H
#define IGT_PRIMES_H

unsigned long igt_next_prime_number(unsigned long x);

#define for_each_prime_number(prime, count)				\
	for (unsigned long prime = 0, count__ = (count);		\
	     count__-- && (prime = igt_next_prime_number(prime)); )

#endif /* IGT_PRIMES_H */
